import subprocess
from flask import Flask

app = Flask(__name__)

def ping_decorator(url):
    def decorator(func):
        def wrapper():
            try:
                result = subprocess.run(['ping', '-c', '1', url], capture_output=True, text=True, timeout=5)
                pong = result.returncode
                if pong == 0:
                    return f"{url} is alive"
                else:
                    return f"{url} is not alive"
            except subprocess.TimeoutExpired:
                return f"{url} ping timeout"
            except Exception as e:
                return f"Error pinging {url}: {str(e)}"
        return wrapper
    return decorator

