from flask import send_file
import os

def serve_image(filename):
    def decorator(func):
        def wrapper(*args, **kwargs):
            image_path = os.path.join('images', filename)
            if os.path.exists(image_path):
                return send_file(image_path)
            return func(*args, **kwargs)
        return wrapper
    return decorator

