def calculate_price():
    yaml_payload = request.args.get('payload')
    if not yaml_payload:
        return yaml.dump({'error': 'No payload provided'}), 400

    try:
        data = yaml.safe_load(yaml_payload)
        product_code = data.get('product_code')
        quantity = data.get('quantity', 0)

        if not product_code or quantity <= 0:
            return yaml.dump({'error': 'Invalid product code or quantity'}), 400

        unit_price = get_price_for_product_code(product_code)
        total_price = unit_price * quantity

        return yaml.dump({'total_price': total_price}), 200

    except yaml.YAMLError:
        return yaml.dump({'error': 'Invalid YAML format'}), 400

