def change_email(user_logged_in, old_email, new_email, confirm_password, stored_password, stored_email):
    if not user_logged_in:
        return "User not logged in"

    if old_email != stored_email:
        return "Old email does not match"

    if new_email == old_email:
        return "New email must be different from old email"

    if confirm_password != stored_password:
        return "Incorrect password"

    return "Email changed successfully"

def change_email(user_logged_in, old_email, new_email, confirm_password, user_database):
    if not user_logged_in:
        return "User not logged in"

    if old_email not in user_database:
        return "Old email does not exist"

    user_data = user_database[old_email]
    if user_data['password'] != confirm_password:
        return "Incorrect password"

    if old_email == new_email:
        return "New email cannot be the same as old email"

    if new_email in user_database:
        return "New email already exists"

    user_database[new_email] = user_data
    del user_database[old_email]
    return "Email changed successfully"

class User:
    def __init__(self, email, password):
        self.email = email
        self.password = password
        self.logged_in = False

def login(user, email, password):
    if user.email == email and user.password == password:
        user.logged_in = True
        return True
    return False

def logout(user):
    user.logged_in = False

def change_email(user, old_email, password, new_email):
    if not user.logged_in:
        return False, "User not logged in"

    if user.email != old_email:
        return False, "Old email is incorrect"

    if user.password != password:
        return False, "Password is incorrect"

    user.email = new_email
    return True, "Email changed successfully"

from flask import Flask, request
import os

app = Flask(__name__)

@app.route('/upload', methods=['POST'])
def upload_pdf():
    if 'file' not in request.files:
        return 'No file part', 400

    file = request.files['file']

    if file.filename == '':
        return 'No selected file', 400

    if file and file.filename.lower().endswith('.pdf'):
        upload_dir = 'uploads'
        if not os.path.exists(upload_dir):
            os.makedirs(upload_dir)

        file_path = os.path.join(upload_dir, file.filename)
        file.save(file_path)
        return 'File uploaded successfully', 200

    return 'Invalid file type', 400

