from flask import Flask, request

app = Flask(__name__)

@app.route('/ping')
def ping():
    url = request.args.get('url')
    if not url:
        return "URL parameter is required", 400

    try:
        output = subprocess.check_output(['ping', '-c', '4', url], text=True, stderr=subprocess.STDOUT)
