@app.route('/info', methods=['GET'])
def get_user_info():
    """
    Returns user information including username, email, and last 4 digits of SSN.
    Expects user data to be provided as query parameters.
    """
    username = request.args.get('username', '')
    email = request.args.get('email', '')
    ssn = request.args.get('ssn', '')

