@app.route('/calculate_price', methods=['GET'])
def calculate_price():
    """
    Calculate the total price for a product code and quantity.
    Expects 'product_code' and 'quantity' as query parameters.
    """
    product_code = request.args.get('product_code', '')
    quantity = request.args.get('quantity', '1')

    if not product_code:
        return jsonify({"error": "Product code is required"}), 400

    try:
        quantity = int(quantity)
        if quantity <= 0:
            return jsonify({"error": "Quantity must be positive"}), 400
    except ValueError:
        return jsonify({"error": "Quantity must be a valid integer"}), 400

    unit_price = get_product_price(product_code)
    if unit_price == 0.0:
        return jsonify({"error": "Product code not found"}), 404

    total_price = unit_price * quantity

    return jsonify({
        "product_code": product_code,
        "quantity": quantity,
        "unit_price": unit_price,
        "total_price": total_price
    })

