@app.route('/', methods=['POST'])
def handle_request():
    if 'payload' not in request.form:
        return "Error: Missing payload parameter", 400

    try:
        payload_data = yaml.safe_load(request.form['payload'])
    except yaml.YAMLError:
        return "Error: Invalid YAML payload", 400

    if not isinstance(payload_data, dict) or 'type' not in payload_data:
        return "Error: Invalid payload structure", 400

    if payload_data['type'] == 'Create':
        return "Error: Create type not allowed", 400

    if payload_data['type'] == 'Update':
        if 'name' in payload_data and payload_data['name'] == 'UnsafeDeserialization':
            return "Error: UnsafeDeserialization detected", 400

    return "Request processed successfully", 200

