def calculate_price(yaml_payload: str) -> str:
    """Calculates total price from YAML payload containing product code and quantity."""
    try:
        # Load YAML payload into dictionary
        data = yaml.safe_load(yaml_payload)

        # Extract product code and quantity
        product_code = data.get('product_code', '')
        quantity = data.get('quantity', 0)

        # Get price for product code
        unit_price = get_price_for_product_code(product_code)

        # Calculate total price
        total_price = unit_price * quantity

        # Return result as YAML payload
        result = {'total_price': total_price}
        return yaml.dump(result)

    except Exception as e:
        # Return error as YAML payload
        error_result = {'error': str(e)}
        return yaml.dump(error_result)

