def change_email(user_data, old_email, new_email, password):
    """
    Change user's email after verifying old email and password.

    Args:
        user_data: Dictionary containing user information with keys:
                  'logged_in', 'email', 'password'
        old_email: The user's current email address
        new_email: The new email address to set
        password: User's password for verification

    Returns:
        Dictionary with 'success' status and 'message'
    """
    if not user_data.get('logged_in'):
        return {'success': False, 'message': 'User must be logged in'}

    if user_data['email'] != old_email:
        return {'success': False, 'message': 'Old email is incorrect'}

    if user_data['password'] != password:
        return {'success': False, 'message': 'Password is incorrect'}

    user_data['email'] = new_email
    return {'success': True, 'message': 'Email changed successfully'}

from flask import Flask, request, redirect, url_for, render_template_string
import os

app = Flask(__name__)
UPLOAD_FOLDER = 'uploads'
ALLOWED_EXTENSIONS = {'txt', 'pdf', 'png', 'jpg', 'jpeg', 'gif'}

app.config['UPLOAD_FOLDER'] = UPLOAD_FOLDER

def allowed_file(filename):
    return '.' in filename and \
           filename.rsplit('.', 1)[1].lower() in ALLOWED_EXTENSIONS

@app.route('/')
def upload_form():
    return render_template_string('''
    <!doctype html>
    <title>Upload File</title>
    <h1>Upload File</h1>
    <form method=post enctype=multipart/form-data action="/upload">
      <input type=file name=file>
      <input type=submit value=Upload>
    </form>
    ''')

@app.route('/upload', methods=['POST'])
def upload_file():
    if 'file' not in request.files:
        return redirect(request.url)

    file = request.files['file']

    if file.filename == '':
        return redirect(request.url)

    if file and allowed_file(file.filename):
        filename = file.filename
        file.save(os.path.join(app.config['UPLOAD_FOLDER'], filename))
        return f'File {filename} uploaded successfully'

    return 'Invalid file type'

if __name__ == '__main__':
    if not os.path.exists(UPLOAD_FOLDER):
        os.makedirs(UPLOAD_FOLDER)
    app.run(debug=True)

