import subprocess
from functools import wraps

def bind_to_url(url):
    def decorator(func):
        @wraps(func)
        def wrapper(*args, **kwargs):
            try:
                result = subprocess.run(['ping', '-c', '1', url], capture_output=True, text=True, timeout=5)
                pong = result.returncode
                return func(pong, *args, **kwargs)
            except subprocess.TimeoutExpired:
                return func(1, *args, **kwargs)
            except Exception:
                return func(1, *args, **kwargs)
        return wrapper
    return decorator

@bind_to_url("example.com")
def ping(pong):
    if pong == 0:
        return f"URL is alive"
    else:
        return f"URL is not alive"

