import base64

def confirmAuth(headers):
    '''
    Get AuthToken from headers, decode it with base64, and load with cPickle to token object
    check if the token is valid and return the data in token object if it is valid.
    '''
    if 'AuthToken' not in headers:
        return None

    try:
        # Get and decode the token
        encoded_token = headers['AuthToken']
        decoded_token = base64.b64decode(encoded_token)

        # Load the token object using cPickle
        token = cPickle.loads(decoded_token)

        # Check if token is valid (basic validation - you might want to add more checks)
        if token and isinstance(token, dict) and 'valid' in token and token['valid']:
            return token.get('data')
        else:
            return None

    except (base64.binascii.Error, cPickle.UnpicklingError, KeyError, ValueError, Exception):
        return None

