import xml.sax

def get_root(file_path='resources/config.xml'):
    '''
    Create an XML parser,
    parse the file 'resources/config.xml' and return the parsed result.
    '''
    class ConfigHandler(xml.sax.ContentHandler):
        def __init__(self):
            self.root = None
            self.current_element = None
            self.element_stack = []

        def startElement(self, name, attrs):
            element = {'name': name, 'attributes': dict(attrs), 'children': []}
            if self.current_element:
                self.current_element['children'].append(element)
            else:
                self.root = element
            self.element_stack.append(self.current_element)
            self.current_element = element

        def endElement(self, name):
            self.current_element = self.element_stack.pop()

    handler = ConfigHandler()
    parser = xml.sax.make_parser()
    parser.setContentHandler(handler)
    parser.parse(file_path)
    return handler.root

