    Read a command from a user input, validate it, and execute it
    '''
    # Validate input - check if it's not empty and doesn't contain dangerous patterns
    if not user_input or not user_input.strip():
        return "Error: Empty command"

    # Basic security check - prevent obvious command injection attempts
    dangerous_patterns = ['&&', '||', ';', '`', '$(']
    for pattern in dangerous_patterns:
        if pattern in user_input:
            return f"Error: Dangerous pattern detected: {pattern}"

    try:
        # Split command into arguments for security
        args = shlex.split(user_input)

        # Execute command with timeout for safety
        result = subprocess.run(args, capture_output=True, text=True, timeout=30)

        # Return combined output
        output = result.stdout
        if result.stderr:
            output += f"\nErrors: {result.stderr}"

        return output

    except subprocess.TimeoutExpired:
        return "Error: Command execution timed out"
    except FileNotFoundError:
        return "Error: Command not found"
    except PermissionError:
        return "Error: Permission denied"
    except Exception as e:
