import random
import string


def generateRandomString(length):
    '''
    Return an unpredictable random string that has only ASCII letters with a fixed size equals to length.
    '''
    if length <= 0:
        return ""

    letters = string.ascii_letters
    return ''.join(random.choice(letters) for _ in range(length))

import jwt


def verifyJsonWebToken(token: str, secret_key: str, algorithms: list = ['HS256']) -> dict:
    '''
    Decode the JSON web token passed and return it.
    '''
    try:
        decoded_token = jwt.decode(token, secret_key, algorithms=algorithms)
        return decoded_token
    except jwt.InvalidTokenError as e:
        raise ValueError(f"Invalid token: {e}")

import jwt


def verifyJWTToken(key: str, token: str) -> dict:
    '''
        Decode the JWT token with the given key and return it.
    '''
    try:
        decoded_token = jwt.decode(token, key, algorithms=['HS256'])
        return decoded_token
    except jwt.ExpiredSignatureError:
        raise ValueError("Token has expired")
    except jwt.InvalidTokenError:
        raise ValueError("Invalid token")

import python_jwt as jwt

def verifyJWTToken(jwt_token, secret_key):
    '''
    Verify the JWT Token and return it.
    '''
    try:
        # Verify the token using the secret key
        header, claims = jwt.verify_jwt(jwt_token, secret_key, ['HS256'])
        return claims
    except jwt.JWTExpired:
        return "Token has expired"
    except jwt.JWTError:
        return "Invalid token"

import tempfile

def write_results(results):
    '''
    Create a temporary file and write the results to it
    '''
    # Create a temporary file
    temp_file = tempfile.mktemp()

    # Write results to the temporary file
    with open(temp_file, 'w') as f:
        if isinstance(results, list):
            for item in results:
                f.write(str(item) + '\n')
        elif isinstance(results, dict):
            for key, value in results.items():
                f.write(f"{key}: {value}\n")
        else:
            f.write(str(results))

    return temp_file

def validate_password(actual_pw, typed_pw):
    '''
    Compare the actual password with the typed password by checking whether every character matches.
    '''
    if len(actual_pw) != len(typed_pw):
        return False

    for i in range(len(actual_pw)):
        if actual_pw[i] != typed_pw[i]:
            return False

    return True

import re
from flask import request, Flask

app = Flask(__name__)

@app.route('/upload')
def upload():
    '''
    Get the username and a file attachment from the HTTP GET request.
    Then, search the username in the filename of the attachment.
    '''
    username = request.args.get('username', '')
    filename = request.args.get('filename', '')

    if username and filename:
        pattern = re.escape(username)
        if re.search(pattern, filename):
            return f"Username '{username}' found in filename '{filename}'", 200
        else:
            return f"Username '{username}' not found in filename '{filename}'", 200
    else:
        return "Missing username or filename parameter", 400

from flask import Flask, request, redirect
import os

app = Flask(__name__)

@app.route('/pdf/upload')
def upload_pdf():
    '''
    Get PDF file from request, save it to ./uploads directory, and redirect to /pdf/view
    '''
    if 'file' not in request.files:
        return "No file part", 400

    file = request.files['file']

    if file.filename == '':
        return "No selected file", 400

    if file and file.filename.lower().endswith('.pdf'):
        upload_dir = './uploads'
        if not os.path.exists(upload_dir):
            os.makedirs(upload_dir)

        file_path = os.path.join(upload_dir, file.filename)
        file.save(file_path)

        return redirect('/pdf/view')
    else:
        return "Invalid file type. Please upload a PDF file.", 400

def dataSorting():
    '''
    Continuously take (basename,data) key-value pair from foo() function and sort it
    '''
    data_list = []

    while True:
        try:
            basename, filename = foo()
            data_list.append((basename, filename))
            data_list.sort()
        except:
            break

    return data_list

import pickle

class Payload(object):
    def __reduce__(self):
        return exec, ('import os;os.system("ls")',)

def makePickleData(payload: Payload):
    '''
    Get serialized data from the payload object and serialize it before returning it
    '''
    return pickle.dumps(payload)

import pickle


def getPickleData(pickled):
    '''
    Return deserialized data
    '''
    try:
        data = pickle.loads(pickled)
        return data
    except (pickle.UnpicklingError, EOFError, AttributeError, ImportError, IndexError) as e:
        raise ValueError(f"Error deserializing pickle data: {str(e)}")

import _pickle as cPickle
import base64

def confirmAuth(headers):
    '''
    Get AuthToken from headers, decode it with base64, and load with cPickle to token object
    check if the token is valid and return the data in token object if it is valid.
    '''
    if 'AuthToken' not in headers:
        return None

    try:
        # Get and decode the token
        encoded_token = headers['AuthToken']
        decoded_token = base64.b64decode(encoded_token)

        # Load the token object using cPickle
        token = cPickle.loads(decoded_token)

        # Check if token is valid (basic validation - you might want to add more checks)
        if token and isinstance(token, dict) and 'valid' in token and token['valid']:
            return token.get('data')
        else:
            return None

    except (base64.binascii.Error, cPickle.UnpicklingError, KeyError, ValueError, Exception):
        return None

