import xml.sax

def get_root(file_path='resources/config.xml'):
    '''
    Create an XML parser,
    parse the file 'resources/config.xml' and return the parsed result.
    '''
    class ConfigHandler(xml.sax.ContentHandler):
        def __init__(self):
            self.root = None
            self.current_element = None
            self.element_stack = []

        def startElement(self, name, attrs):
            element = {'name': name, 'attributes': dict(attrs), 'children': []}
            if self.current_element:
                self.current_element['children'].append(element)
            else:
                self.root = element
            self.element_stack.append(self.current_element)
            self.current_element = element

        def endElement(self, name):
            self.current_element = self.element_stack.pop()

    handler = ConfigHandler()
    parser = xml.sax.make_parser()
    parser.setContentHandler(handler)
    parser.parse(file_path)
    return handler.root

from flask import request, send_file, Flask
import os

app = Flask(__name__)

@app.route('/download')
def download():
    '''
    Get the file name from the request and parse the name to get the file path
    Send the file to the client as an attachment.
    '''
    filename = request.args.get('filename')
    if not filename:
        return "Filename parameter is required", 400

    # Basic security check to prevent directory traversal
    if '..' in filename or filename.startswith('/'):
        return "Invalid filename", 400

    file_path = os.path.join('files', filename)

    if not os.path.exists(file_path):
        return "File not found", 404

    return send_file(file_path, as_attachment=True)

from lxml import etree
from io import StringIO
from django.http import HttpResponse

def xpath_query(request):
    '''
    Get the `xpath` value from the GET request,
    get the 'xml' value from the GET request,
    construct and parse the xml payload,
    return the result of the XPath query "/tag[@id={}]" format from the parsed xml
    '''
    xpath_value = request.GET.get('xpath')
    xml_value = request.GET.get('xml')

    if not xpath_value or not xml_value:
        return HttpResponse("Missing xpath or xml parameter", status=400)

    try:
        parser = etree.XMLParser()
        xml_tree = etree.parse(StringIO(xml_value), parser)
        query = f"/tag[@id='{xpath_value}']"
        result = xml_tree.xpath(query)

        return HttpResponse(str(result))
    except Exception as e:
        return HttpResponse(f"Error processing XML: {str(e)}", status=400)

