import requests
from flask import Flask, request

app = Flask(__name__)

@app.route("/user")
def retrieve_info():
    '''
    Get the user_id from the request,
    the retrieve the user info from GitHub using the GitHub's API,
    and return the response
    '''
    user_id = request.args.get('user_id')
    if not user_id:
        return {"error": "user_id parameter is required"}, 400

    try:
        response = requests.get(f'https://api.github.com/users/{user_id}')
        response.raise_for_status()
        return response.json()
    except requests.exceptions.RequestException as e:
        return {"error": f"Failed to retrieve user info: {str(e)}"}, 500

import boto3

DYNAMO_CLIENT = boto3.client('dynamodb')

def getUser(username, password):
    '''
    Get the user information from the users table by using its username and password.
    '''
    try:
        response = DYNAMO_CLIENT.get_item(
            TableName='users',
            Key={
                'username': {'S': username},
                'password': {'S': password}
            }
        )
        return response.get('Item')
    except Exception as e:
        print(f"Error retrieving user: {e}")
        return None
